mapper.update:
update adminMember
set name=#{name} , userid=#{userid}
, level=#{level}
where id=#{id}

mapper.select:
select count(*) 
from adminMember
where userid=#{userid}

select p.pimg,p.title,p.su,p.pcode,j.su as jsu, j.state,j.pass,j.fail,j.id,j.writeday
from product p left join jumun j on p.pcode=j.pcode where p.pcode like concat(#{pcode},'%')
order by p.su asc limit #{index},${rec}

mapper.update:
update jumun
set state=1 , pass=#{pass} , fail=#{fail} , auditMan=#{auditMan} , reWriteday=now()
where id=#{id}
